{"type":"assistant","message":{"id":"msg_20250925133348c7adff82396c488d","type":"message","role":"assistant","model":"glm-4.5","content":[{"type":"tool_use","id":"call_4jius8s8vqd","name":"Write","input":{"file_path":"/config/workspace/mcp-repl/optimized-test-1758778250974-baseline-refactoring/hooks/useTasks.ts","content":"'use client'\n\nimport { useState, useCallback } from 'react'\nimport { Task } from '@/types/task'\nimport { generateId } from '@/lib/utils'\nimport { STRINGS } from '@/lib/constants'\n\nexport function useTasks() {\n  const [tasks, setTasks] = useState<Task[]>([])\n  const [newTask, setNewTask] = useState({\n    title: '',\n    description: '',\n    priority: 'medium' as const\n  })\n  const [filter, setFilter] = useState<'all' | 'active' | 'completed'>('all')\n\n  const addTask = useCallback(() => {\n    if (!newTask.title.trim()) return\n\n    const task: Task = {\n      id: generateId(),\n      title: newTask.title,\n      description: newTask.description,\n      priority: newTask.priority,\n      completed: false,\n      createdAt: new Date()\n    }\n\n    setTasks(prev => [...prev, task])\n    setNewTask({ title: '', description: '', priority: 'medium' })\n  }, [newTask])\n\n  const toggleTask = useCallback((id: string) => {\n    setTasks(prev => prev.map(task =>\n      task.id === id ? { ...task, completed: !task.completed } : task\n    ))\n  }, [])\n\n  const deleteTask = useCallback((id: string) => {\n    setTasks(prev => prev.filter(task => task.id !== id))\n  }, [])\n\n  const filteredTasks = tasks.filter(task => {\n    if (filter === 'active') return !task.completed\n    if (filter === 'completed') return task.completed\n    return true\n  })\n\n  const taskCounts = {\n    all: tasks.length,\n    active: tasks.filter(t => !t.completed).length,\n    completed: tasks.filter(t => t.completed).length\n  }\n\n  return {\n    tasks: filteredTasks,\n    taskCounts,\n    newTask,\n    setNewTask,\n    filter,\n    setFilter,\n    addTask,\n    toggleTask,\n    deleteTask,\n    strings: STRINGS.tasks\n  }\n}"}}],"stop_reason":null,"stop_sequence":null,"usage":{"input_tokens":423,"output_tokens":748,"cache_read_input_tokens":28134}},"parent_tool_use_id":null,"session_id":"e6a69200-3f26-43db-9d89-6dbc290f213a","uuid":"2b063753-a7f1-48cd-8e9c-725179f03fdb"}
