{"type":"assistant","message":{"id":"msg_20250925133416b1a62c38b4c84d11","type":"message","role":"assistant","model":"glm-4.5","content":[{"type":"tool_use","id":"call_9gvlpqmqwrs","name":"Write","input":{"file_path":"/config/workspace/mcp-repl/optimized-test-1758778250974-baseline-refactoring/components/ui/error-boundary.tsx","content":"'use client'\n\nimport React, { Component, ReactNode } from 'react'\nimport { Card, CardContent, CardHeader, CardTitle } from './card'\nimport { Button } from './button'\n\ninterface Props {\n  children: ReactNode\n  fallback?: ReactNode\n}\n\ninterface State {\n  hasError: boolean\n  error?: Error\n}\n\nexport class ErrorBoundary extends Component<Props, State> {\n  constructor(props: Props) {\n    super(props)\n    this.state = { hasError: false }\n  }\n\n  static getDerivedStateFromError(error: Error): State {\n    return { hasError: true, error }\n  }\n\n  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {\n    console.error('Error caught by boundary:', error, errorInfo)\n  }\n\n  render() {\n    if (this.state.hasError) {\n      if (this.props.fallback) {\n        return this.props.fallback\n      }\n\n      return (\n        <Card className=\"max-w-md mx-auto\">\n          <CardHeader>\n            <CardTitle className=\"text-destructive\">Something went wrong</CardTitle>\n          </CardHeader>\n          <CardContent className=\"space-y-4\">\n            <p className=\"text-sm text-muted-foreground\">\n              {this.state.error?.message || 'An unexpected error occurred'}\n            </p>\n            <Button\n              onClick={() => this.setState({ hasError: false, error: undefined })}\n              variant=\"outline\"\n            >\n              Try again\n            </Button>\n          </CardContent>\n        </Card>\n      )\n    }\n\n    return this.props.children\n  }\n}"}}],"stop_reason":null,"stop_sequence":null,"usage":{"input_tokens":323,"output_tokens":601,"cache_read_input_tokens":2954