export class ToolError extends Error {
  constructor(message, code = 'TOOL_ERROR', toolName = 'unknown', retryable = false, suggestions = []) {
    super(message);
    this.name = 'ToolError';
    this.code = code;
    this.tool = toolName;
    this.timestamp = Date.now();
    this.retryable = retryable;
    this.suggestions = suggestions;
  }
