{"type":"assistant","message":{"id":"msg_2025091922494021ac27f6acaa4705","type":"message","role":"assistant","model":"glm-4.5","content":[{"type":"tool_use","id":"call_qwc2gooffbo","name":"Edit","input":{"file_path":"/config/workspace/mcp-repl/optimized-test-1758293145424-baseline-refactoring/hooks/useTasks.ts","old_string":"import { useState, useCallback } from 'react'\nimport { Task, TaskPriority, TaskFilter } from '@/lib/types'\nimport { generateId } from '@/lib/utils'\n\nexport function useTasks() {\n  const [tasks, setTasks] = useState<Task[]>([])\n  const [filter, setFilter] = useState<TaskFilter>('all')\n\n  const addTask = useCallback((title: string, description: string = '', priority: TaskPriority = 'medium') => {\n    if (!title.trim()) return\n\n    const newTask: Task = {\n      id: generateId(),\n      title: title.trim(),\n      description: description.trim(),\n      priority,\n      completed: false,\n      createdAt: new Date()\n    }\n\n    setTasks(prev => [...prev, newTask])\n  }, [])\n\n  const toggleTask = useCallback((id: string) => {\n    setTasks(prev => prev.map(task =>\n      task.id === id ? { ...task, completed: !task.completed } : task\n    ))\n  }, [])\n\n  const deleteTask = useCallback((id: string) => {\n    setTasks(prev => prev.filter(task => task.id !== id))\n  }, [])\n\n  const filteredTasks = tasks.filter(task => {\n    if (filter === 'active') return !task.completed\n    if (filter === 'completed') return task.completed\n    return true\n  })\n\n  const taskStats = {\n    total: tasks.length,\n    active: tasks.filter(t => !t.completed).length,\n    completed: tasks.filter(t => t.completed).length\n  }\n\n  return {\n    tasks: filteredTasks,\n    allTasks: tasks,\n    filter,\n    setFilter,\n    addTask,\n    toggleTask,\n    deleteTask,\n    taskStats\n  }\n}","new_string":"import { useState, useCallback } from 'react'\nimport { Task, TaskPriority, TaskFilter } from '@/lib/types'\nimport { generateId } from '@/lib/utils'\n\n