ask\n    ))\n  }\n\n  const deleteTask = (id: string) => {\n    setTasks(prev => prev.filter(task => task.id !== id))\n  }\n\n  const filteredTasks = tasks.filter(task => {\n    if (filter === 'active') return !task.completed\n    if (filter === 'completed') return task.completed\n    return true\n  })","new_string":"'use client'\nimport React, { useState } from 'react'\nimport { Card, CardContent, CardHeader, CardTitle } from './ui/card'\nimport { Button } from './ui/button'\nimport { Input } from './ui/input'\nimport { formatDate } from '@/lib/utils'\nimport { STRINGS, PRIORITIES, TASK_FILTERS } from '@/lib/constants'\nimport { useTasks } from '@/hooks/useTasks'\nimport type { TaskFilter } from '@/types/task'\n\nexport function TaskManager() {\n  const [newTask, setNewTask] = useState({ title: '', description: '', priority: PRIORITIES.MEDIUM })\n  const [filter, setFilter] = useState<TaskFilter>(TASK_FILTERS.ALL)\n\n  const { tasks, addTask, toggleTask, deleteTask, getTaskCounts } = useTasks()\n  const taskCounts = getTaskCounts()\n\n  const handleAddTask = () => {\n    if (!newTask.title.trim()) return\n    addTask({\n      title: newTask.title,\n      description: newTask.description,\n      priority: newTask.priority as 'low' | 'medium' | 'high'\n    })\n    setNewTask({ title: '', description: '', priority: PRIORITIES.MEDIUM })\n  }\n\n  const filteredTasks = tasks.filter(task => {\n    if (filter === TASK_FILTERS.ACTIVE) return !task.completed\n    if (filter === TASK_FILTERS.COMPLETED) return task.completed\n    return true\n  })"}}],"stop_reason":"tool_use","stop_sequence":null,"usage":{"input_tokens":766,"output_tokens":1362,"cache_read_input_tokens":47326}},"parent_tool_use_id":null,"session_id":"9d1568b4-606a-4885-b00a-4909dde7f67d","uuid":"52aae25d-b22e-430a-befa-d3863eded0aa"}
