    language = 'javascript',
    recursive = false,
    maxResults = 100,
    ignorePatterns = []
  } = options;

  try {
    if (!fs.existsSync(filePath)) {
      throw new Error(`File not found: ${filePath}`);
    }

const matches = await helper.searchPattern(content, pattern);

        return matches.map(match => ({
          file,
          content: match.text,
          line: match.line,
          column: match.column,
          start: match.start,
          end: match.end
        }));
      } catch (error) {
