          {
            "type": "command",
            "command": "node $CLAUDE_PROJECT_DIR/src/core/hooks/check-context-requirements.js \"$CLAUDE_PROMPT\""
          }
        ]
      }
    ],
    "SessionStart": [
      {
        "matcher": ".*",
        "hooks": [
          {
            "type": "command",
            "command": "node $CLAUDE_PROJECT_DIR/src/core/hooks/context-initialization.js"
          },
          {
            "type": "command",
            "command": "node $CLAUDE_PROJECT_DIR/src/core/hooks/project-discovery.js"
          },
          {
            "type": "command",
            "command": "node $CLAUDE_PROJECT_DIR/src/core/hooks/context-summary.js"
          }
        ]
      }
    ],
    "Notification": [
      {
        "matcher": "save|commit",
        "hooks": [
          {
            "type": "command",
            "command": "node $CLAUDE_PROJECT_DIR/src/core/hooks/lint-before-save.js"
          }
        ]
      }
    ]
  }
