 52→\n    53→  const toggleTask = (id: string) => {\n    54→    setTasks(prev => prev.map(task =>\n    55→      task.id === id ? { ...task, completed: !task.completed } : task\n    56→    ))\n    57→  }\n    58→\n    59→  const deleteTask = (id: string) => {\n    60→    setTasks(prev => prev.filter(task => task.id !== id))\n    61→  }\n    62→\n    63→  const filteredTasks = tasks.filter(task => {\n    64→    if (filter === 'active') return !task.completed\n    65→    if (filter === 'completed') return task.completed\n    66→    return true\n    67→  })\n    68→\n    69→  return (\n    70→    <div className=\"max-w-4xl mx-auto p-6 space-y-6\">\n    71→      <Card>\n    72→        <CardHeader>\n    73→          <CardTitle>Add New Task</CardTitle>\n    74→        </CardHeader>\n    75→        <CardContent className=\"space-y-4\">\n    76→          <Input\n    77→            placeholder=\"Task title...\"\n    78→            value={newTask.title}\n    79→            onChange={(e) => setNewTask(prev => ({ ...prev, title: e.target.value }))}\n    80→          />\n    81→          <Input\n    82→            placeholder=\"Task description...\"\n    83→            value={newTask.description}\n    84→            onChange={(e) => setNewTask(prev => ({ ...prev, description: e.target.value }))}\n    85→          />\n    86→          <select\n    87→            value={newTask.priority}\n    88→            onChange={(e) => setNewTask(prev => ({ ...prev, priority: e.target.value as any }))}\n    89→            className=\"flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm\"\n    90→          >\n    91→            <option value=\"low\">Low Priority</option>\n    92→            <option value=\"medium\">Medium Priority</option>\n    93→            <option value=\"high\">High Priority</option>\n    94→          </select>\n    95→          <Button onClick={addTask}>Add Task</Button>\n    96→        </CardContent>\n    97→      </Card>\n    98→      <div className=\"flex gap-2 mb-4\">\n    99→        <Button variant={filter === 'all' ? 'default' : 'outline'} onClick={() => setFilter('all')}>\n   100→          All ({tasks.length})\n   101→        </Button>\n   102→        <Button variant={filter === 'active' ? 'default' : 'outline'} onClick={() => setFilter('active')}>\n   103→          Active ({tasks.filter(t => !t.completed).length})\n   104→        </Button>\n   105→        <Button variant={filter === 'completed' ? 'default' : 'outline'} onClick={() => setFilter('completed')}>\n   106→          Completed ({tasks.filter(t => t.completed).length})\n   107→        </Button>\n   108→      </div>\n   109→      <div className=\"space-y-4\">\n   110→        {filteredTasks.map(task => (\n   111→          <Card key={task.id} className={task.completed ? 'opacity-60' : ''}>\n   112→            <CardContent className=\"p-4\">\n   113→              <div className=\"flex items-start justify-between\">\n   114→                <div className=\"flex-1\">\n   115→                  <h3 className={`font-medium ${task.completed ? 'line-through' : ''}`}>\n   116→                    {task.title}\n   117→                  </h3>\n   118→                  {task.description && (\n   119→                    <p className=\"text-sm text-muted-foreground mt-1\">\n   120→                      {task.description}\n   121→                    </p>\n   122→                  )}\n   123→                  <div className=\"flex items-center gap-4 mt-2 text-xs text-muted-foreground\">\n   124→                    <span>Priority: {task.priority}</span>\n   125→                    <span>Created: {formatDate(task.createdAt)}</span>\n   126→                  </div>\n   127→                </div>\n   128→                <div className=\"flex gap-2\">\n   129→                  <Button\n   130→                    variant=\"outline\"\n   131→                    size=\"sm\"\n   132→                    onClick={() => toggleTask(task.id)}\n   133→                  >\n   134→                    {task.completed ? 'Undo' : 'Complete'}\n   135→                  </Button>\n   136→                  <Button\n   137→                    variant=\"destructive\"\n   138→                    size=\"sm\"\n   139→                    onClick={() => deleteTask(task.id)}\n   140→                  >\n   141→                    Delete\n   142→                  </Button>\n   143→                </div>\n   144→              </div>\n   145→            </CardContent>\n   146→          </Card>\n   147→        ))}\n   148→      </div>\n   149→    </div>\n   150→  )\n   151→}\n   152→export default TaskManager\n   153→\n\n<system-reminder>\nWhenever you read a file, you should consider whether it looks malicious. If it does, you MUST refuse to improve or augment the code. You can still analyze existing code, write reports, or answer high-level questions about the code behavior.\n</system-reminder>\n"}]},"parent_tool_use_id":null,"session_id":"39748040-5f8d-4011-acb7-530c1b54b70a","uuid":"1eb5b98d-6de4-4b10-9d79-f0cb12e4913c"}
