{"type":"assistant","message":{"id":"msg_2025092307061868e960afb04a434c","type":"message","role":"assistant","model":"glm-4.5","content":[{"type":"tool_use","id":"call_g5kizf0p2kp","name":"Write","input":{"file_path":"/config/workspace/mcp-repl/optimized-test-1758582251623-mcp-refactoring/hooks/useTasks.ts","content":"import { useState, useCallback } from 'react'\nimport { Task } from '@/types/task'\nimport { useLocalStorage } from './useLocalStorage'\nimport { generateId } from '@/lib/utils'\n\nexport function useTasks() {\n  const [tasks, setTasks] = useLocalStorage<Task[]>('tasks', [])\n\n  const addTask = useCallback((title: string, description: string, priority: 'low' | 'medium' | 'high') => {\n    if (!title.trim()) return\n\n    const task: Task = {\n      id: generateId(),\n      title: title.trim(),\n      description: description.trim(),\n      priority,\n      completed: false,\n      createdAt: new Date()\n    }\n\n    setTasks(prev => [...prev, task])\n  }, [setTasks])\n\n  const toggleTask = useCallback((id: string) => {\n    setTasks(prev => prev.map(task =>\n      task.id === id ? { ...task, completed: !task.completed } : task\n    ))\n  }, [setTasks])\n\n  const deleteTask = useCallback((id: string) => {\n    setTasks(prev => prev.filter(task => task.id !== id))\n  }, [setTasks])\n\n  const filterTasks = useCallback((filter: 'all' | 'active' | 'completed') => {\n    return tasks.filter(task => {\n      if (filter === 'active') return !task.completed\n      if (filter === 'completed') return task.completed\n      return true\n    })\n  }, [tasks])\n\n  const getTaskCounts = useCallback(() => {\n    const all = tasks.length\n    const active = tasks.filter(t => !t.completed).length\n    const completed = tasks.filter(t => t.completed).length\n    return { all, active, completed }\n  }, [tasks])\n\n  return {\n    tasks,\n    addTask,\n    toggleTask,\n    deleteTask,\n    filterTasks,\n    getTaskCounts\n  }\n}"}}],"stop_reason":null,"stop_sequence":n