{"type":"assistant","message":{"id":"msg_2025092307062495543bf233f74bdf","type":"message","role":"assistant","model":"glm-4.5","content":[{"type":"tool_use","id":"call_xf8p6fibfc","name":"Write","input":{"file_path":"/config/workspace/mcp-repl/optimized-test-1758582251623-mcp-refactoring/components/ui/error-boundary.tsx","content":"import React, { Component, ErrorInfo, ReactNode } from 'react'\nimport { Card, CardContent, CardHeader, CardTitle } from './card'\nimport { Button } from './button'\n\ninterface Props {\n  children: ReactNode\n  fallback?: ReactNode\n}\n\ninterface State {\n  hasError: boolean\n  error: Error | null\n  errorInfo: ErrorInfo | null\n}\n\nexport class ErrorBoundary extends Component<Props, State> {\n  public state: State = {\n    hasError: false,\n    error: null,\n    errorInfo: null\n  }\n\n  public static getDerivedStateFromError(error: Error): State {\n    return { hasError: true, error, errorInfo: null }\n  }\n\n  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {\n    console.error('Uncaught error:', error, errorInfo)\n    this.setState({\n      error,\n      errorInfo\n    })\n  }\n\n  private handleReset = () => {\n    this.setState({ hasError: false, error: null, errorInfo: null })\n  }\n\n  public render() {\n    if (this.state.hasError) {\n      if (this.props.fallback) {\n        return this.props.fallback\n      }\n\n      return (\n        <div className=\"p-4\">\n          <Card className=\"border-red-200 bg-red-50\">\n            <CardHeader>\n              <CardTitle className=\"text-red-800\">Something went wrong</CardTitle>\n            </CardHeader>\n            <CardContent className=\"space-y-4\">\n              <p className=\"text-red-700\">\n                {this.state.error?.message || 'An unexpected error occurred'}\n              </p>\n              <details className=\"text-sm text-red-600\">\n                <summary className=\"cursor-pointer\">Error details</summary>\n                <pre className=\"mt-2 whitespace-pre-wrap\">\n                  {this.state.error?.stack}\n                </pre>\n              </details>\n              <Button onClick={this.handleReset} variant=\"outline\">\n                Try again\n              </Button>\n            </CardContent>\n          </Card>\n        </div>\n      )\n    }\n\n    return this.props.children\n  }\n}"}}],"stop_reason":null,"stop_sequence":null,"usage":{"input_tokens":238,"output_tokens":778,"cache_read_input_tokens":35218}},"parent_tool_use_id":null,"session_id":"39748040-5f8d-4011-acb7-530c1b54b70a","uuid":"d7379256-d386-44fe-be93-7ee93523f462"}
