{"type":"assistant","message":{"id":"msg_20250923070703857dd6e59d3a41a2","type":"message","role":"assistant","model":"glm-4.5","content":[{"type":"tool_use","id":"call_rk8ro031vcp","name":"Write","input":{"file_path":"/config/workspace/mcp-repl/optimized-test-1758582251623-mcp-refactoring/components/app-error-boundary.tsx","content":"import React, { Component, ErrorInfo, ReactNode } from 'react'\nimport { Card, CardContent, CardHeader, CardTitle } from './ui/card'\nimport { Button } from './ui/button'\n\ninterface Props {\n  children: ReactNode\n}\n\ninterface State {\n  hasError: boolean\n  error: Error | null\n  errorInfo: ErrorInfo | null\n}\n\nexport class AppErrorBoundary extends Component<Props, State> {\n  public state: State = {\n    hasError: false,\n    error: null,\n    errorInfo: null\n  }\n\n  public static getDerivedStateFromError(error: Error): State {\n    return { hasError: true, error, errorInfo: null }\n  }\n\n  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {\n    console.error('Application error:', error, errorInfo)\n    this.setState({\n      error,\n      errorInfo\n    })\n  }\n\n  private handleReset = () => {\n    this.setState({ hasError: false, error: null, errorInfo: null })\n  }\n\n  public render() {\n    if (this.state.hasError) {\n      return (\n        <div className=\"min-h-screen flex items-center justify-center p-4\">\n          <Card className=\"w-full max-w-md border-red-200 bg-red-50\">\n            <CardHeader>\n              <CardTitle className=\"text-red-800\">Application Error</CardTitle>\n            </CardHeader>\n            <CardContent className=\"space-y-4\">\n              <p className=\"text-red-700\">\n                Something went wrong in the application. Please try refreshing the page.\n              </p>\n              {this.state.error && (\n                <details className=\"text-sm text-red-600\">\n                  <summary className=\"cursor-pointer\">Error details</summary>\n                  <pre className=\"mt-2 whitespace-pre-wrap\">\n                    {this.state.error.stack}\n                  </pre>\n                </details>\n              )}\n              <div className=\"flex gap-2\">\n                <Button onClick={this.handleReset} variant=\"outline\">\n                  Try Again\n                </Button>\n                <Button onClick={() => window.location.reload()}>\n                  Refresh Page\n                </Button>\n              </div>\n            </CardContent>\n          </Card>\n        </div>\n      )\n    }\n\n    return this.props.children\n  }\n}"}}],"stop_reason":"tool_use","stop_sequence":null,"usage":{"input_tokens":340,"output_tokens":844,"cache_read_input_tokens":44713}},"parent_tool_use_id":null,"session_id":"39748040-5f8d-4011-acb7-530c1b54b70a","uuid":"aae41e9a-f9fb-4433-932c-cd091d98dd43"}
